import createSendToken from '../helpers/createSendToken';
import models from '../models';
import bcrypt from 'bcryptjs';


exports.login = async (req, res, next) => {
  const {email, password} = req.body;

  // 1)Check if email & password exist.
  if (!email || !password) {
    // eslint-disable-next-line max-len
    return res.status(400).json({status: 'fail', message: 'Please provide email and password!'});
  }

  // 2)Check if user exist and password is correct

  const user = await models.User.findOne({where: {email}});
  // eslint-disable-next-line max-len
  if (!user) return res.status(401).json({status: 'fail', message: 'Incorrect email or password'});
  const correctPassword = async function(candidatePassword, userPassword) {
    return await bcrypt.compare(candidatePassword, userPassword);
  };

  if (!user || !(await correctPassword(password, user.password))) {
    // eslint-disable-next-line max-len
    return res.status(401).json({status: 'fail', message: 'Incorrect email or password'});
  }
  createSendToken(user, 200, res);
};

import jwt from 'jsonwebtoken';
const signToken = (id) =>
  jwt.sign({id}, process.env.JWT_SECRET, {
    expiresIn: process.env.JWT_EXPIRES_IN,
  });
const createSendToken = (user, statusCode, res) => {
  const token = signToken(user._id);

  // remove the password from the output
  user.password = undefined;

  res.status(statusCode).json({
    status: 'success',
    token,
    data: {
      user,
    },
    message: 'Login Successful',
  });
};

export default createSendToken;

import {readdirSync} from 'fs';
import {basename as _basename, join} from 'path';
import Sequelize, {DataTypes} from 'sequelize';
import prop from '../config/config';

const basename = _basename(__filename);
const env = process.env.NODE_ENV || 'development';
const config = prop[env];
const db = {};

let sequelize;
if (config.url) {
  sequelize = new Sequelize(config.url, {
    dialect: 'postgres',
  });
} else {
  sequelize = new Sequelize(
      config.database,
      config.username,
      config.password,
      config,
  );
}

readdirSync(__dirname)
    .filter(
        (file) =>
          // eslint-disable-next-line max-len
          file.indexOf('.') !== 0 && file !== basename && file.slice(-3) === '.js',
    )
    .forEach((file) => {
      const model = require(join(__dirname, file))(sequelize, DataTypes);
      db[model.name] = model;
    });

Object.keys(db).forEach((modelName) => {
  if (db[modelName].associate) {
    db[modelName].associate(db);
  }
});

db.sequelize = sequelize;
db.Sequelize = Sequelize;
sequelize
    .authenticate()
    .then(() => {
      console.log('Connected! Database Status : ON ðŸ”¥.');
    })
    .catch((err) => {
      console.error('Failed to connect! Database Status : OFF:', err);
    });

export default db;

