import express from 'express'
import morgan from "morgan";
import swaggerUi from "swagger-ui-express";
import swaggerDoc from "./data.json";
import cors from "cors";
import i18next from 'i18next';
import Backend from 'i18next-fs-backend';
import middleware from 'i18next-http-middleware';
import cookieParser from 'cookie-parser';

import userRoute from './routes/updateProfile'

i18next
  .use(Backend)
  .use(middleware.LanguageDetector)
  .init({
    fallbackLng: "en",
    backend: {
      loadPath: "./locales/{{lng}}/translation.json",
    },
  });

const server=express();
server.use(middleware.handle(i18next));
server.use(express.json());

server.get("/", (req, res) => {
  res.status(200).json({
    success: req.t('success'),
    message: req.t('message'),
  });
});

server.use(morgan("dev"));
server.use(cors());
server.use(cookieParser())

server.use("/api-docs", swaggerUi.serve, swaggerUi.setup(swaggerDoc, { explorer: true }));
// server.use("*", (req, res, next) => {
// 	res.status(404).json({ error: "NOT FOUND", });
// });
server.use("/profile",userRoute);

export default server;

import express from 'express'
import {getUser,updateUser} from '../controllers/user';

const router = express.Router()


router.get('/:id', getUser);
router.post('/update/:id', updateUser);

export default router

import dotenv from 'dotenv';
import model from '../models';

const User = model.User;
dotenv.config();

const  getUser = async (req, res) => {
    const { id } = req.params;

    const user = await User.findOne({
      where: {
        id,
      },
    });

    if (!user) {
      return res.status(400).send({
        message: req.t('noUserFound')+' '+id
      });
    }

    return res.send(user);
  };

  const updateUser = async (req, res) => {
    const { name, email, phone, password } = req.body;
    const { id } = req.params;

    const user = await User.findOne({
      where: {
        id,
      },
    });

    if (!user) {
      return res.status(400).send({
        message: req.t('noUserFound')+' '+id
      });
    }

    try {
      if (name) {
        user.name = name;
      }

      if (email) {
          user.email = email;
        }
      if (phone) {
        user.phone = phone;
      }
      if(password){
        user.password = password;  
      }

      user.save();
      return res.send({
        message: req.t('profileUpdate')
      });
    } catch (err) {
      return res.status(500).send({
        message: `Error: ${err.message}`,
      });
    }
  };
  export { getUser, updateUser };

import fs from 'fs';
import path from 'path';
import Sequelize from 'sequelize';
import enVariables from '../config/config';

const basename = path.basename(__filename);
const env = process.env.NODE_ENV || 'development';
const config = enVariables[env];
const db = {};

let sequelize;
if (config.url) {
  sequelize = new Sequelize(config.url, {
    dialect:"postgress"
  });
} else {
  sequelize = new Sequelize(config.database, config.username, config.password, config);
}

fs
  .readdirSync(__dirname)
  .filter(file => (file.indexOf('.') !== 0) && (file !== basename) && (file.slice(-3) === '.js'))
  .forEach(file => {
    const model = require(path.join(__dirname, file)).default(sequelize, Sequelize.DataTypes);
    db[model.name] = model;
  });

Object.keys(db).forEach(modelName => {
  if (db[modelName].associate) {
    db[modelName].associate(db);
  }
});

db.sequelize = sequelize;
db.Sequelize = Sequelize;

sequelize
.authenticate()  
.then(() => {    console.log('DB connected') })  
.catch((err) => {    console.error('Failed to connect!', err);  });

export default db;

